\n#headermenu .switch li.list a.selected {background-position: -1px -39px; }\n#headermenu .switch li.grid a.selected {background-position: -49px -39px }\n\n/* =================================================\n  Main\n================================================= */\n#main {\n  border-top: 1px dotted #B5B5B6;\n  border-bottom: 1px dotted #B5B5B6;\n}\n\n#main_contents {\n  border-left: 1px dotted #B5B5B6;\n  border-right: 1px dotted #B5B5B6;\n}\n\n/*  content\n-------------------------------------------------- */\n#main_contents .content {\n  position: relative;\n  margin-bottom: 20px;\n  padding: 15px 0 20px;\n  background-color: #FFFFFF; /* メインカラムのコンテンツ背景色 */\n  border-top: 1px dotted #B5B5B6; /* メインカラムのコンテンツ上のライン */\n  border-bottom: 1px dotted #B5B5B6; /* メインカラムのコンテンツ下のライン */\n}\n\n#main_contents h2.entry_header,\n#main_contents h2.sub_header {\n  margin: 0 0 15px;\n  padding: 5px 0 5px 35px;\n  background: url(https://blog-imgs-43.fc2.com/t/e/m/templates/content_title.png) no-repeat 20px 5px; /* 記事タイトル前のアイコン */\n}\n\n#main_contents h2.entry_header a,\n#main_contents h2.sub_header a {\n  display: block;\n  width: 100%;\n  height: 1.4em;\n  overflow: hidden;\n  white-space: nowrap;\n  text-overflow: ellipsis;\n}\n\n#main_contents h3.sub_header {\n  margin: -15px 0 20px;\n  padding: 10px 20px 10px 35px;\n  background: #F4F4F4 url(https://blog-imgs-43.fc2.com/t/e/m/templates/content_title.png) no-repeat 15px 10px; /* メインカラムのプラグインタイトル前のアイコン */\n  border-bottom: 1px dotted #B5B5B6;\n}\n\n#main_contents h4.sub_title {\n  padding: 3px 0 3px 15px;\n  margin-bottom: 20px;\n  background: url(https://blog-imgs-43.fc2.com/t/e/m/templates/content_title.png) no-repeat left 2px; /* コメント・TB見出し前のアイコン */\n}\n\n/* content style */\n#main_contents blockquote {\n  margin-bottom: 10px;\n  padding: 15px;\n  background-color: #F4F4F4;\n}\n\n#main_contents input {\n  padding: 2px;\n}\n\n/* entry_date  */\n#main_contents .entry_date {\n  float: left;\n  width: 170px;\n  margin: 0 0 10px 20px;\n  text-align: left;\n}\n\n#main_contents .entry_date li {\n  display: inline;\n  margin-right: 1em;\n}\n\n/* entry_state */\n#main_contents .entry_state {\n  float: right;\n  overflow: hidden;\n  width: 140px;\n  margin: 0 20px 10px 0;\n  text-align: right;\n}\n\n#main_contents .content .entry_state li {\n  display: inline;\n  margin-left: 1em;\n  padding-right: 1em;\n  border-right: 1px solid #999999;\n}\n\n#main_contents .content .entry_state li:last-child {\n  padding-right: 0;\n  border-right: none;\n}\n\n/* entry_body  */\n#main_contents .entry_body {\n  clear: both;\n  overflow: hidden;\n  margin: 0 20px;\n  padding-bottom: 40px;\n  zoom: 100%;\n}\n\n#main_contents .entry_body:after {\n  content: \"\";\n  clear: both;\n  height: 0;\n  display: block;\n  visibility: hidden;\n}\n\n#main_contents .entry_body .entry_image {\n  position: absolute;\n  left: 20px;\n}\n\n#main_contents .entry_body .entry_image img {\n  max-width: 200px; /* リスト表示の時のサムネイルの幅の上限 */\n  max-height: 250px;\n  border: 4px solid #FFFFFF;\n  -webkit-box-shadow: 0px 1px 1px 0px #999999;\n  -moz-box-shadow: 0px 1px 1px 0px #999999;\n  box-shadow: 0px 1px 1px 0px #999999;\n}\n\n#main_contents .entry_body .entry_discription {\n  margin-left: 238px; /* サムネイル幅を変更した場合はこちらも調整して下さい */\n  min-height: 228px;\n}\n\n#main_contents .entry_body .entry_more {\n  position: absolute;\n  right: 20px;\n  bottom: 10px;\n  padding: 15px 0;\n  text-align: right;\n}\n\n#main_contents #e .entry_body .entry_more { display:none; }\n\n#main_contents .entry_body .entry_more a { /* 「続きを読む」リンク */\n  display: inline-brock;\n  margin-top: 10px;\n  padding: 5px 10px;\n  background: #EAEAEA;\n  border-top: 1px solid #DCDDDD;\n  border-radius: 3px;\n  -webkit-border-radius: 3px;\n  -moz-border-radius: 3px;\n  text-align: center;\n}\n\n#main_contents .entry_body .entry_more a:hover { /* 「続きを読む」リンクにポインタをのせた時の設定 */\n  background: #8ADAE6;\n  border-top: 1px solid #6BA9B3;\n  color: #FFFFFF;\n}\n\n#main_contents .entry_body .more {\n  margin-top: 10px;\n}\n\n#main_contents .entry_body .fc2_footer {\n  margin-top: 10px;\n}\n\n/* Baton */\ndl.blog_baton {\n  border: 1px solid #EFEFEF;\n  padding: 20px 20px 10px;\n}\n\ndt.baton_q {\n  padding-bottom: 5px;\n}\n\ndd.baton_a {\n  margin-bottom: 10px;\n  padding-bottom: 5px;\n  border-bottom: 1px solid #EFEFEF;\n}\n\n/* relate 関連記事リンク */\n.relate_dl {margin-top:25px;}\n.relate_dl dt {\n  font-size:124%;\n  margin-bottom: 10px;\n  padding-left: 5px;\n  border-left: 5px solid #CCC;\n}\n\n/* fc2twtr */\n.fc2twtr {\n  zoom:100%;\n}\n\n.fc2twtr:after {\n  content: \"\";\n  clear: both;\n  height: 0;\n  display: block;\n  visibility: hidden;\n}\n\n.fc2twtr dt {\n  margin-bottom: 20px;\n}\n\n.fc2twtr dd {\n  margin-bottom: 5px;\n  padding-bottom: 5px;\n  border-bottom: 1px solid #EFEFEF;\n  color: #666666;\n}\n\n/* entry_footer */\n#main_contents .entry_footer {\n  position: absolute;\n  bottom: 0;\n  width: 100%;\n  padding: 15px 0;\n  background: #F4F4F4; /* 個別ページ 記事フッタの背景色 */\n  zoom: 100%;\n}\n\n#main_contents .entry_footer:after {\n  content: \"\";\n  clear: both;\n  height: 0;\n  display: block;\n  visibility: hidden;\n}\n\n#main_contents .entry_footer .entry_state {\n  margin-bottom: 0;\n  width: auto;\n}\n\n/* titlelist search */\n#main_contents .list_body {\n  padding: 0 20px;\n}\n\n#main_contents .list_body li {\n  margin-bottom: 5px;\n  padding-bottom: 5px;\n  border-bottom: 1px solid #EFEFEF;\n}\n\n/* sub content */\n#main_contents .sub_content {\n  margin-bottom: 20px;\n  padding: 20px;\n  border-bottom: 1px dotted #B5B5B6;\n}\n\n#main_contents .sub_content .sub_footer {\n  margin-top: 20px;\n  text-align: right;\n}\n\n#main_contents .sub_content .sub_footer li {\n  display: inline;\n  margin-left: 1em;\n}\n\n#main_contents .sub_content .sub_footer li a { color: #999999; }\n\n#main_contents .sub_content .sub_footer li a:hover { color: #8ADAE6; }\n\n/* form */\n#main_contents .form {\n  margin: 0 20px;\n}\n\n/* trackback */\n#main_contents .trackback {\n  margin: 0 20px;\n}\n#main_contents .trackback input {\n  width: 396px;\n}\n\n/* list_content \n-------------------------------------------------- */\n#main_contents .list_content {\n  float: none;\n}\n\n/* grid_content\n-------------------------------------------------- */\n#main_contents .grid_content {\n  float: left;\n  margin-left: -1px;\n  margin-right: -1px;\n  width: 50%;\n  height: 310px; /* グリッド表示の時の記事の高さ */\n  border-left: 1px dotted #B5B5B6;\n  border-right: 1px dotted #B5B5B6;\n}\n#main_contents .grid_content .fc2blomaga_buy_area {\n  display:none;\n}\n#main_contents .grid_content .entry_body {\n  overflow: hidden;\n  height: 5.8em;\n}\n#main_contents #e,#main_contents #e .entry_body { height: auto; }\n\n#main_contents .p_area,#main_contents .p_area .entry_body {\n  height: auto;\n}\n\n#main_contents .grid_content .entry_body .entry_image img {\n  max-width: 150px; /* グリッド表示の時のサムネイルの幅の上限 */\n  max-height: 210px;\n}\n\n#main_contents .grid_content .entry_body .entry_discription {\n  margin-left: 178px; /* ↑の.entry_image imgのmax-widthを変更した場合はこちらも調整して下さい */\n}\n\n#main_contents .grid_content:first-child, #main_contents .p_area {\n  float: none;\n  width: 100%;\n  min-height: 110px;\n  margin-left: 0;\n  margin-right: 0;\n  border-left: none;\n  border-right: none;\n}\n\n#main_contents .no_br:first-child br { display: none; }\n\n#main_contents .p_area .entry_body {\n  height: auto;\n}\n#main_contents .p_area .entry_body .fc2blomaga_buy_area {\n  display:block;\n}\n\n#main_contents .grid_content:first-child .entry_image img, #main_contents .p_area .entry_image img {\n  max-width: 200px; /* グリッド表示の時の最初の記事のサムネイルの幅の上限 */\n  max-height: 220px;\n}\n\n#main_contents .grid_content:first-child .entry_discription {\n  margin-left: 238px; /* ↑の.entry_image imgのmax-widthを変更した場合はこちらも調整して下さい */\n  min-height: 228px;\n}\n\n/* page_navi\n-------------------------------------------------- */\n#main_contents .page_navi {\n  position:relative;\n  clear: both;\n  height: 123px;\n  margin-top: -20px;\n  text-align: center;\n  zoom:100%;\n}\n\n#main_contents .page_navi:after {\n  content: \"\";\n  clear: both;\n  height: 0;\n  display: block;\n  visibility: hidden;\n}\n\n#main_contents .page_navi a {\n  display: block;\n  overflow: hidden;\n  height: 18px;\n  width: 123px;\n  padding-top: 45px;\n  padding-bottom: 60px;\n  background: url(https://blog-imgs-43.fc2.com/t/e/m/templates/page_navi.png) no-repeat 0 0;\n}\n\n#main_contents .page_navi a:hover { color: #8ADAE6; }\n\n#main_contents .page_navi .prev {\n  float: left;\n  width: 30%;\n  background-position: left -145px;\n    text-align: left;\n\n}\n\n#main_contents .page_navi .next {\n  float: right;\n  width: 30%;\n  background-position: right -145px;\n  text-align: right;\n}\n\n#main_contents .page_navi .home {\n  position: absolute;\n  left:50%;\n  margin-left: -61px;\n  background-position: -375px -145px;\n  text-indent: 100%; \n  white-space: nowrap; \n  overflow: hidden;\n}\n\n#main_contents .page_navi a.prev:hover { background-position: left 15px; }\n#main_contents .page_navi a.next:hover { background-position: right 15px; }\n#main_contents .page_navi a.home:hover { background-position: -375px 15px; }\n\n#main_contents .page_navi .prevpage,\n#main_contents .page_navi .nextpage {\n  text-align: left;\n  text-indent: -100%; \n  white-space: nowrap; \n  overflow: hidden;\n  white-space:nowrap;\n  text-overflow: ellipsis;\n}\n\n#main_contents .page_navi .preventry {\n  padding-left:80px;\n}\n#main_contents .page_navi .nextentry {\n  padding-right: 80px;\n}\n\n/* =================================================\n  Entry (html tag)\n================================================= */\n.p_area .entry_body h2, .p_area .entry_body h3,\n.p_area .entry_body h4, .p_area .entry_body h5 {\n  background: url(https://blog-imgs-43.fc2.com/t/e/m/templates/content_title.png) no-repeat 5px 5px;\n  margin: 0 0 10px;\n  padding: 5px 0 5px 20px;\n}\n.p_area .entry_body ul,\n.p_area .entry_body ol { margin: 1em 0em 1em; }\n.p_area .entry_body ul li {\n  list-style: disc outside;\n  margin-left: 2em;\n}\n.p_area .entry_body ol li {\n  list-style: decimal outside;\n  margin-left: 2em;\n}\n.p_area .entry_body table {\n  border-collapse: collapse;\n}\n.p_area .entry_body table th,\n.p_area .entry_body table td {\n  padding: 5px;\n}\n.p_area .entry_body table th { background-color: #EFEFEF; }\n\n/* =================================================\n  Sidemenu\n================================================= */\n#sidemenu {\n  border-left: 1px dotted #B5B5B6;\n  border-right: 1px dotted #B5B5B6;\n}\n\n#sidemenu .sidemenu_content {\n  margin-bottom: 10px;\n  padding: 10px;\n  background-color: #FFFFFF; /* サイドメニュー(プラグイン)の背景色、アイコン */\n  border-bottom: 1px dotted #B5B5B6;\n}\n\n#sidemenu h3 {\n  margin: -10px -10px 0;\n  padding: 10px 10px 10px 25px;\n  background: #F4F4F4 url(https://blog-imgs-43.fc2.com/t/e/m/templates/content_title.png) no-repeat 10px 10px; /* サイドメニュー(プラグイン)のタイトルの背景色、アイコン */\n  border-top: 1px dotted #B5B5B6;\n  border-bottom: 1px dotted #B5B5B6;\n}\n\n/* =================================================\n  pagetop\n================================================= */\n#pagetop {\n  position: fixed;\n  right:200px;\n  bottom:50px;\n  z-index: 100;\n}\n\n#pagetop a {\n  display: block;\n  width: 58px;\n  height: 58px;\n  background: #EEEEEE url(https://blog-imgs-43.fc2.com/t/e/m/templates/page_top.png) no-repeat 13px 13px;\n  border-radius: 10px;\n  -webkit-border-radius: 10px;\n  -moz-border-radius: 10px;\n  opacity: 0.7;\n  filter: alpha(opacity=70);\n  text-indent: 100%;\n  white-space: nowrap;\n  overflow: hidden; \n}\n\n#pagetop a:hover {\n  opacity: 1;\n  background-position:13px -48px;\n}\n\n/* =================================================\n  plgin\n================================================= */\n.plg .plg_description,\n.plg .plg_body,\n.plg .plg_footer {\n  margin-top: 10px;\n}\n\n#main_contents .plg .plg_description,\n#main_contents .plg .plg_body,\n#main_contents .plg .plg_footer {\n  margin-left: 20px;\n  margin-right: 20px;\n}\n\n.plg li {\n  margin-bottom: 5px;\n}\n\n.plg input {\n  margin: 2px;\n}\n\n/* plugin-search\n------------------------------------------------- */\n.plg .plugin-search input[type=\"text\"] {\n  width: 90%;\n}\n\n/* Calendar(Plugin)\n------------------------------------------------- */\n.plg table.calender {\n  width: 100%;\n  margin: 0px auto;\n  font-size: 100%;\n}\n\n.plg table.calender caption {\n  margin-bottom: 10px;\n  margin: 0px auto;\n}\n\n.plg table.calender th,\n.plg table.calender td {\n  padding: 0.1em;\n  border-bottom: 1px solid #EFEFEF;\n  text-align: center;\n}\n\n.plg table.calender td a {\n  display: block;\n  background-color: #EFEFEF;\n}\n\n.plg table.calender td a:hover {\n  background-color: #8ADAE6;\n  color: #ffffff;\n}\n\n.plg table.calender th#sun {\n  color: #FF0000;\n}\n\n.plg table.calender th#sat {\n  color: #0000FF;\n}\n\n/* Piyo(Plugin)\n------------------------------------------------- */\n.plg .mylog {\n  padding: 10px;\n  background: #F9F9F9;\n  border: 1px solid #EFEFEF;\n}\n\n.plg .mylog .title {\n  padding-bottom: 5px;\n  margin-bottom: 10px;\n  border-bottom: 1px solid #EFEFEF;\n  font-weight: bold;\n}\n\n.plg .mylog dl {\n  margin: 0px;\n  padding: 0px;\n}\n\n.plg .mylog dt.day {\n  margin: 0px;\n  padding: 0px 0px 5px;\n  background-image: none;\n  text-align: left;\n  font-size: 85% !important;  /* for Modern browser */\n  font-size: 93%;  /* for IE6 */\n}\n\n.plg .mylog dt.day img {\n  vertical-align: middle;\n}\n\n.plg .mylog dd.piyolog {\n  margin: 0px 0px 5px;\n  padding: 0px 0px 10px;\n  border-bottom: 1px solid #EFEFEF;\n}\n\n.plg .tab {\n  text-align: right;\n  margin-bottom: 10px;\n}\n\n.plg .tab input{\n  padding: 2px 5px;\n}\n\n.plg .tab input.b1{\n  margin-right: 5px;\n}\n\n.plg .license {\n  text-align: right;\n  font-size: 85% !important;  /* for Modern browser */\n  font-size: 93%;  /* for IE6 */\n}\n\n/* =================================================\n  Footer\n================================================= */\n#footer {\n  background: #EFEFEF; /* フッターの背景色 */\n  -webkit-box-shadow: 0px 1px 3px 0px #CCC inset;\n  -moz-box-shadow: 0px 1px 3px 0px #CCC inset;\n  box-shadow: 0px 1px 3px 0px #CCC inset;\n  text-shadow: 0px 1px 2px #FFFFFF;\n}\n\n/* footer_plg\n------------------------------------------------- */\n#footer_plg {\n  padding-bottom: 40px;\n  border-left: 1px dotted #9FA0A0;\n  zoom:100%;\n}\n\n#footer_plg:after {\n  content: \"\";\n  clear: both;\n  height: 0;\n  display: block;\n  visibility: hidden;\n}\n\n#footer_plg .footer_content {\n  float: left;\n  width: 199px;\n  padding: 0 20px;\n  border-right: 1px dotted #9FA0A0;\n}\n\n#footer_plg h3 {\n  margin-bottom: 10px;\n}\n\n/* footer_inner\n------------------------------------------------- */\n#footer_inner /* フッター(コピーライト等) */ { color: #B5B5B6; }\n\n/* =================================================\n  for MacIE\n================================================= */\n/*\\*//*/\ntextarea, select, input {\n  font-family:Osaka;\n}\n/**/\n\ntextarea,input,select {\n  font-family: \"ＭＳ Ｐゴシック\", \"Osaka\", \"ヒラギノ角ゴ Pro W3\", \"Hiragino Kaku GothicPro\", sans-serif;\n}\n\n',1,'2020-07-24 12:09:11','2021-01-28 09:33:38');
