ntent: \"\";\r\n  clear: both;\r\n  height: 0;\r\n  display: block;\r\n  visibility: hidden;\r\n}\r\n\r\n#main_contents .entry_footer .entry_state {\r\n  margin-bottom: 0;\r\n  width: auto;\r\n}\r\n\r\n/* titlelist search */\r\n#main_contents .list_body {\r\n  padding: 0 20px;\r\n}\r\n\r\n#main_contents .list_body li {\r\n  margin-bottom: 5px;\r\n  padding-bottom: 5px;\r\n  border-bottom: 1px solid #EFEFEF;\r\n}\r\n\r\n/* sub content */\r\n#main_contents .sub_content {\r\n  margin-bottom: 20px;\r\n  padding: 20px;\r\n  border-bottom: 1px dotted #B5B5B6;\r\n}\r\n\r\n#main_contents .sub_content .sub_footer {\r\n  margin-top: 20px;\r\n  text-align: right;\r\n}\r\n\r\n#main_contents .sub_content .sub_footer li {\r\n  display: inline;\r\n  margin-left: 1em;\r\n}\r\n\r\n#main_contents .sub_content .sub_footer li a { color: #999999; }\r\n\r\n#main_contents .sub_content .sub_footer li a:hover { color: #8ADAE6; }\r\n\r\n/* form */\r\n#main_contents .form {\r\n  margin: 0 20px;\r\n}\r\n\r\n/* trackback */\r\n#main_contents .trackback {\r\n  margin: 0 20px;\r\n}\r\n#main_contents .trackback input {\r\n  width: 396px;\r\n}\r\n\r\n/* list_content \r\n-------------------------------------------------- */\r\n#main_contents .list_content {\r\n  float: none;\r\n}\r\n\r\n/* grid_content\r\n-------------------------------------------------- */\r\n#main_contents .grid_content {\r\n  float: left;\r\n  margin-left: -1px;\r\n  margin-right: -1px;\r\n  width: 50%;\r\n  height: 310px; /* グリッド表示の時の記事の高さ */\r\n  border-left: 1px dotted #B5B5B6;\r\n  border-right: 1px dotted #B5B5B6;\r\n}\r\n#main_contents .grid_content .fc2blomaga_buy_area {\r\n  display:none;\r\n}\r\n#main_contents .grid_content .entry_body {\r\n  overflow: hidden;\r\n  height: 5.8em;\r\n}\r\n#main_contents #e,#main_contents #e .entry_body { height: auto; }\r\n\r\n#main_contents .p_area,#main_contents .p_area .entry_body {\r\n  height: auto;\r\n}\r\n\r\n#main_contents .grid_content .entry_body .entry_image img {\r\n  max-width: 150px; /* グリッド表示の時のサムネイルの幅の上限 */\r\n  max-height: 210px;\r\n}\r\n\r\n#main_contents .grid_content .entry_body .entry_discription {\r\n  margin-left: 178px; /* ↑の.entry_image imgのmax-widthを変更した場合はこちらも調整して下さい */\r\n}\r\n\r\n#main_contents .grid_content:first-child, #main_contents .p_area {\r\n  float: none;\r\n  width: 100%;\r\n  min-height: 110px;\r\n  margin-left: 0;\r\n  margin-right: 0;\r\n  border-left: none;\r\n  border-right: none;\r\n}\r\n\r\n#main_contents .no_br:first-child br { display: none; }\r\n\r\n#main_contents .p_area .entry_body {\r\n  height: auto;\r\n}\r\n#main_contents .p_area .entry_body .fc2blomaga_buy_area {\r\n  display:block;\r\n}\r\n\r\n#main_contents .grid_content:first-child .entry_image img, #main_contents .p_area .entry_image img {\r\n  max-width: 200px; /* グリッド表示の時の最初の記事のサムネイルの幅の上限 */\r\n  max-height: 220px;\r\n}\r\n\r\n#main_contents .grid_content:first-child .entry_discription {\r\n  margin-left: 238px; /* ↑の.entry_image imgのmax-widthを変更した場合はこちらも調整して下さい */\r\n  min-height: 228px;\r\n}\r\n\r\n/* page_navi\r\n-------------------------------------------------- */\r\n#main_contents .page_navi {\r\n  position:relative;\r\n  clear: both;\r\n  height: 123px;\r\n  margin-top: -20px;\r\n  text-align: center;\r\n  zoom:100%;\r\n}\r\n\r\n#main_contents .page_navi:after {\r\n  content: \"\";\r\n  clear: both;\r\n  height: 0;\r\n  display: block;\r\n  visibility: hidden;\r\n}\r\n\r\n#main_contents .page_navi a {\r\n  display: block;\r\n  overflow: hidden;\r\n  height: 18px;\r\n  width: 123px;\r\n  padding-top: 45px;\r\n  padding-bottom: 60px;\r\n  background: url(https://blog-imgs-43.fc2.com/t/e/m/templates/page_navi.png) no-repeat 0 0;\r\n}\r\n\r\n#main_contents .page_navi a:hover { color: #8ADAE6; }\r\n\r\n#main_contents .page_navi .prev {\r\n  float: left;\r\n  width: 30%;\r\n  background-position: left -145px;\r\n    text-align: left;\r\n\r\n}\r\n\r\n#main_contents .page_navi .next {\r\n  float: right;\r\n  width: 30%;\r\n  background-position: right -145px;\r\n  text-align: right;\r\n}\r\n\r\n#main_contents .page_navi .home {\r\n  position: absolute;\r\n  left:50%;\r\n  margin-left: -61px;\r\n  background-position: -375px -145px;\r\n  text-indent: 100%; \r\n  white-space: nowrap; \r\n  overflow: hidden;\r\n}\r\n\r\n#main_contents .page_navi a.prev:hover { background-position: left 15px; }\r\n#main_contents .page_navi a.next:hover { background-position: right 15px; }\r\n#main_contents .page_navi a.home:hover { background-position: -375px 15px; }\r\n\r\n#main_contents .page_navi .prevpage,\r\n#main_contents .page_navi .nextpage {\r\n  text-align: left;\r\n  text-indent: -100%; \r\n  white-space: nowrap; \r\n  overflow: hidden;\r\n  white-space:nowrap;\r\n  text-overflow: ellipsis;\r\n}\r\n\r\n#main_contents .page_navi .preventry {\r\n  padding-left:80px;\r\n}\r\n#main_contents .page_navi .nextentry {\r\n  padding-right: 80px;\r\n}\r\n\r\n/* =================================================\r\n  Entry (html tag)\r\n================================================= */\r\n.p_area .entry_body h2, .p_area .entry_body h3,\r\n.p_area .entry_body h4, .p_area .entry_body h5 {\r\n  background: url(https://blog-imgs-43.fc2.com/t/e/m/templates/content_title.png) no-repeat 5px 5px;\r\n  margin: 0 0 10px;\r\n  padding: 5px 0 5px 20px;\r\n}\r\n.p_area .entry_body ul,\r\n.p_area .entry_body ol { margin: 1em 0em 1em; }\r\n.p_area .entry_body ul li {\r\n  list-style: disc outside;\r\n  margin-left: 2em;\r\n}\r\n.p_area .entry_body ol li {\r\n  list-style: decimal outside;\r\n  margin-left: 2em;\r\n}\r\n.p_area .entry_body table {\r\n  border-collapse: collapse;\r\n}\r\n.p_area .entry_body table th,\r\n.p_area .entry_body table td {\r\n  padding: 5px;\r\n}\r\n.p_area .entry_body table th { background-color: #EFEFEF; }\r\n\r\n/* =================================================\r\n  Sidemenu\r\n================================================= */\r\n#sidemenu {\r\n  border-left: 1px dotted #B5B5B6;\r\n  border-right: 1px dotted #B5B5B6;\r\n}\r\n\r\n#sidemenu .sidemenu_content {\r\n  margin-bottom: 10px;\r\n  padding: 10px;\r\n  background-color: #FFFFFF; /* サイドメニュー(プラグイン)の背景色、アイコン */\r\n  border-bottom: 1px dotted #B5B5B6;\r\n}\r\n\r\n#sidemenu h3 {\r\n  margin: -10px -10px 0;\r\n  padding: 10px 10px 10px 25px;\r\n  background: #F4F4F4 url(https://blog-imgs-43.fc2.com/t/e/m/templates/content_title.png) no-repeat 10px 10px; /* サイドメニュー(プラグイン)のタイトルの背景色、アイコン */\r\n  border-top: 1px dotted #B5B5B6;\r\n  border-bottom: 1px dotted #B5B5B6;\r\n}\r\n\r\n/* =================================================\r\n  pagetop\r\n================================================= */\r\n#pagetop {\r\n  position: fixed;\r\n  right:200px;\r\n  bottom:50px;\r\n  z-index: 100;\r\n}\r\n\r\n#pagetop a {\r\n  display: block;\r\n  width: 58px;\r\n  height: 58px;\r\n  background: #EEEEEE url(https://blog-imgs-43.fc2.com/t/e/m/templates/page_top.png) no-repeat 13px 13px;\r\n  border-radius: 10px;\r\n  -webkit-border-radius: 10px;\r\n  -moz-border-radius: 10px;\r\n  opacity: 0.7;\r\n  filter: alpha(opacity=70);\r\n  text-indent: 100%;\r\n  white-space: nowrap;\r\n  overflow: hidden; \r\n}\r\n\r\n#pagetop a:hover {\r\n  opacity: 1;\r\n  background-position:13px -48px;\r\n}\r\n\r\n/* =================================================\r\n  plgin\r\n================================================= */\r\n.plg .plg_description,\r\n.plg .plg_body,\r\n.plg .plg_footer {\r\n  margin-top: 10px;\r\n}\r\n\r\n#main_contents .plg .plg_description,\r\n#main_contents .plg .plg_body,\r\n#main_contents .plg .plg_footer {\r\n  margin-left: 20px;\r\n  margin-right: 20px;\r\n}\r\n\r\n.plg li {\r\n  margin-bottom: 5px;\r\n}\r\n\r\n.plg input {\r\n  margin: 2px;\r\n}\r\n\r\n/* plugin-search\r\n------------------------------------------------- */\r\n.plg .plugin-search input[type=\"text\"] {\r\n  width: 90%;\r\n}\r\n\r\n/* Calendar(Plugin)\r\n------------------------------------------------- */\r\n.plg table.calender {\r\n  width: 100%;\r\n  margin: 0px auto;\r\n  font-size: 100%;\r\n}\r\n\r\n.plg table.calender caption {\r\n  margin-bottom: 10px;\r\n  margin: 0px auto;\r\n}\r\n\r\n.plg table.calender th,\r\n.plg table.calender td {\r\n  padding: 0.1em;\r\n  border-bottom: 1px solid #EFEFEF;\r\n  text-align: center;\r\n}\r\n\r\n.plg table.calender td a {\r\n  display: block;\r\n  background-color: #EFEFEF;\r\n}\r\n\r\n.plg table.calender td a:hover {\r\n  background-color: #8ADAE6;\r\n  color: #ffffff;\r\n}\r\n\r\n.plg table.calender th#sun {\r\n  color: #FF0000;\r\n}\r\n\r\n.plg table.calender th#sat {\r\n  color: #0000FF;\r\n}\r\n\r\n/* Piyo(Plugin)\r\n------------------------------------------------- */\r\n.plg .mylog {\r\n  padding: 10px;\r\n  background: #F9F9F9;\r\n  border: 1px solid #EFEFEF;\r\n}\r\n\r\n.plg .mylog .title {\r\n  padding-bottom: 5px;\r\n  margin-bottom: 10px;\r\n  border-bottom: 1px solid #EFEFEF;\r\n  font-weight: bold;\r\n}\r\n\r\n.plg .mylog dl {\r\n  margin: 0px;\r\n  padding: 0px;\r\n}\r\n\r\n.plg .mylog dt.day {\r\n  margin: 0px;\r\n  padding: 0px 0px 5px;\r\n  background-image: none;\r\n  text-align: left;\r\n  font-size: 85% !important;  /* for Modern browser */\r\n  font-size: 93%;  /* for IE6 */\r\n}\r\n\r\n.plg .mylog dt.day img {\r\n  vertical-align: middle;\r\n}\r\n\r\n.plg .mylog dd.piyolog {\r\n  margin: 0px 0px 5px;\r\n  padding: 0px 0px 10px;\r\n  border-bottom: 1px solid #EFEFEF;\r\n}\r\n\r\n.plg .tab {\r\n  text-align: right;\r\n  margin-bottom: 10px;\r\n}\r\n\r\n.plg .tab input{\r\n  padding: 2px 5px;\r\n}\r\n\r\n.plg .tab input.b1{\r\n  margin-right: 5px;\r\n}\r\n\r\n.plg .license {\r\n  text-align: right;\r\n  font-size: 85% !important;  /* for Modern browser */\r\n  font-size: 93%;  /* for IE6 */\r\n}\r\n\r\n/* =================================================\r\n  Footer\r\n================================================= */\r\n#footer {\r\n  background: #EFEFEF; /* フッターの背景色 */\r\n  -webkit-box-shadow: 0px 1px 3px 0px #CCC inset;\r\n  -moz-box-shadow: 0px 1px 3px 0px #CCC inset;\r\n  box-shadow: 0px 1px 3px 0px #CCC inset;\r\n  text-shadow: 0px 1px 2px #FFFFFF;\r\n}\r\n\r\n/* footer_plg\r\n------------------------------------------------- */\r\n#footer_plg {\r\n  padding-bottom: 40px;\r\n  border-left: 1px dotted #9FA0A0;\r\n  zoom:100%;\r\n}\r\n\r\n#footer_plg:after {\r\n  content: \"\";\r\n  clear: both;\r\n  height: 0;\r\n  display: block;\r\n  visibility: hidden;\r\n}\r\n\r\n#footer_plg .footer_content {\r\n  float: left;\r\n  width: 199px;\r\n  padding: 0 20px;\r\n  border-right: 1px dotted #9FA0A0;\r\n}\r\n\r\n#footer_plg h3 {\r\n  margin-bottom: 10px;\r\n}\r\n\r\n/* footer_inner\r\n------------------------------------------------- */\r\n#footer_inner /* フッター(コピーライト等) */ { color: #B5B5B6; }\r\n\r\n/* =================================================\r\n  for MacIE\r\n================================================= */\r\n/*\\*//*/\r\ntextarea, select, input {\r\n  font-family:Osaka;\r\n}\r\n/**/\r\n\r\ntextarea,input,select {\r\n  font-family: \"ＭＳ Ｐゴシック\", \"Osaka\", \"ヒラギノ角ゴ Pro W3\", \"Hiragino Kaku GothicPro\", sans-serif;\r\n}\r\n\r\n',1,'2020-10-12 21:15:48','2020-12-28 01:47:15');
INSERT INTO `blog_templates` (`id`, `blog_id`, `template_id`, `title`, `html`, `css`, `device_type`, `created_at`, `updated_at`) VALUES (2,'testblog1',0,'初期テンプレート','<!DOCTYPE html PUBLIC \"-//W3C//DTD XHTML 1.0 Transitional//EN\" \"http://www.w3.org/TR/xhtml1/DTD/xhtml1-transitional.dtd\">\n<html xmlns=\"http://www.w3.org/1999/xhtml\" xml:lang=\"<%template_language>\" lang=\"<%template_language>\">\n<head>\n<meta http-equiv=\"Content-Type\" content=\"text/html; charset=<%template_charset>\" />\n<meta http-equiv=\"Content-Style-Type\" content=\"text/css\" />\n<meta http-equiv=\"Content-Script-Type\" content=\"text/javascript\" />\n<meta http-equiv=\"X-UA-Compatible\" content=\"IE=edge\" />\n<meta name=\"author\" content=\"<%author_name>\" />\n<meta name=\"description\" content=\"<%introduction>\" />\n<title><!--not_index_area--><%sub_title> - <!--/not_index_area--><%blog_name></title>\n<link rel=\"stylesheet\" type=\"text/css\" href=\"<%css_link>\" media=\"all\" />\n<link rel=\"alternate\" type=\"application/rss+xml\" href=\"<%url>?xml\" title=\"RSS\" />\n<link rel=\"top\" href=\"<%url>\" title=\"Top\" />\n<link rel=\"index\" href=\"<%url>?all\" title=\"<%template_index>\" />\n<!--prevpage--><link rel=\"prev\" href=\"<%prevpage_url>\" title=\"<%template_prevpage>\" /><!--/prevpage-->\n<!--nextpage--><link rel=\"next\" href=\"<%nextpage_url>\" title=\"<%template_nextpage>\" /><!--/nextpage-->\n<!--preventry--><link rel=\"next\" href=\"<%preventry_url>\" title=\"<%preventry_title>\" /><!--/preventry-->\n<!--nextentry--><link rel=\"prev\" href=\"<%nextentry_url>\" title=\"<%nextentry_title>\" /><!--/nextentry-->\n<script type=\"text/javascript\" src=\"https://static.fc2.com/js/lib/jquery.js\"></script>\n<script type=\"text/javascript\">\n \n jQuery.noConflict();\n jQuery(function(){\n   \n   // デフォルトで表示するレイアウト  glid or list\n   var DEFAULT_LAYOUT    = \"glid\";\n   \n   // 左カラム(記事)のID\n   var LEFT_COLUMN_ID    = \"#main_contents\";\n   \n   // 右カラム(メニュー)のID\n   var RIGHT_COLUMN_ID   = \"#sidemenu\";\n   \n   // クッキーのキー名\n   var COOKIE_KEY_NAME   = \"template_blog_fc2\";\n   \n   // クッキーのオプション\n   // var COOKIE_OPTION  = { expire: 30, domain: \"myblog.blog.fc2.com\", path: \"/\" };\n   var COOKIE_OPTION     = { expire: 30 };\n   \n   /** フッタープラグイン配置 **/\n   jQuery( \"#footer_plg .plg\" ).equalbox();\n   \n   /** トップへ移動 **/\n   jQuery( \"#toTop\" ).hide();\n   jQuery( \"#pagetop\" ).click(function() {\n     jQuery(\"body, html\").animate({scrollTop: 0}, 800);\n     return false;\n   });\n   \n   // レイアウト切り替えフラグ 連続でレイアウト切り替えを行わせないためのもの\n   var layoutFlag = true;\n   \n   /** 表示切替 **/\n   // list表示\n   function showListLayout() {\n   \n     if ( !layoutFlag ) return;\n     \n     jQuery( LEFT_COLUMN_ID ).css( \"height\", \"\" );\n     jQuery( RIGHT_COLUMN_ID ).css( \"height\", \"\" );\n     \n     var t = setTimeout(function(){\n     \n       layoutFlag = false;\n       \n       jQuery( LEFT_COLUMN_ID )\n         .css( \"opacity\", \"0\" )\n         .show()\n         .fadeTo( \"slow\", 1, function(){ layoutFlag = true; } );\n       \n       jQuery( \".switch .list a\" ).addClass( \"selected\" );\n       jQuery( \".entry\" ).addClass( \"list_content\" );\n       \n       jQuery( \".switch .grid a\" ).removeClass( \"selected\" );\n       jQuery( \".entry\" ).removeClass( \"grid_content\" );\n     \n       jQuery.cookie( COOKIE_KEY_NAME, \"list\", COOKIE_OPTION );\n       \n       equalizeBoxHeight();\n       \n       clearTimeout(t);\n     }, 100);\n     \n     return false;\n   };\n   \n   // glid表示\n   function showGridLayout() {\n     \n     if (!layoutFlag) return;\n     \n     jQuery( LEFT_COLUMN_ID ).css( \"height\", \"\" );\n     jQuery( RIGHT_COLUMN_ID ).css( \"height\", \"\" );\n     \n     var t = setTimeout(function(){\n     \n       layoutFlag = false;\n       \n       jQuery( LEFT_COLUMN_ID )\n         .css( \"opacity\", \"0\" )\n         .show()\n         .fadeTo( \"slow\", 1, function(){ layoutFlag = true; } );\n       \n       jQuery( \".switch .grid a\" ).addClass( \"selected\" );\n       jQuery( \".entry\" ).addClass( \"grid_content\" );\n       \n       jQuery( \".switch .list a\" ).removeClass( \"selected\" );\n       jQuery( \".entry\" ).removeClass( \"list_content\" );\n     \n       jQuery.cookie( COOKIE_KEY_NAME, \"glid\", COOKIE_OPTION );\n       \n       equalizeBoxHeight();\n       \n       clearTimeout(t);\n     }, 100);\n     \n     return false;\n   };\n   \n   jQuery( \".switch .list\" ).click( showListLayout );\n   jQuery( \".switch .grid\" ).click( showGridLayout );\n   \n   // 左カラムと右カラムの高さを合わせる\n   function equalizeBoxHeight() {\n     var leftHeight  = jQuery( LEFT_COLUMN_ID ).height();\n     var rightHeight = jQuery( RIGHT_COLUMN_ID ).height();\n     \n     var height = (leftHeight > rightHeight)? leftHeight: rightHeight;\n     \n     jQuery( LEFT_COLUMN_ID ).height(height + \"px\");\n     jQuery( RIGHT_COLUMN_ID ).height(height + \"px\");\n   };\n   \n   function initialize() {\n     var layout = jQuery.cookie( COOKIE_KEY_NAME );\n     if ( !checkCookieValue( layout ) ) {\n       layout = DEFAULT_LAYOUT;\n     }\n     \n     if ( \"list\" == layout ) {\n       showListLayout();\n     } else if ( \"glid\" == layout ) {\n       showGridLayout();\n     } else {\n       showGridLayout();\n     }\n   };\n   \n   var layoutList = [\"glid\", \"list\"];\n   function checkCookieValue(str) {\n     if (\"string\" == typeof str) {\n       for (var i in layoutList) {\n         if (layoutList[i] == str) return true;\n       }\n     };\n     return false;\n   };\n   \n   initialize();\n });\n</script>\n</head>\n<body>\n<div id=\"container\">\n  <div id=\"header\">\n    <h1><a href=\"<%url>\" accesskey=\"0\" title=\"<%blog_name>\"><%blog_name></a></h1>\n    <p><%introduction></p>\n  </div><!-- /header -->\n  <div id=\"headermenu\">\n    <p class=\"archives\"><a href=\"<%url>archives.html\">記事一覧</a></p>\n    <!--not_titlelist_area-->\n    <!--not_search_area-->\n    <!--not_perm