6;\n}\n\n/*  content\n-------------------------------------------------- */\n#main_contents .content {\n  position: relative;\n  margin-bottom: 20px;\n  padding: 15px 0 20px;\n  background-color: #FFFFFF; /* メインカラムのコンテンツ背景色 */\n  border-top: 1px dotted #B5B5B6; /* メインカラムのコンテンツ上のライン */\n  border-bottom: 1px dotted #B5B5B6; /* メインカラムのコンテンツ下のライン */\n}\n\n#main_contents h2.entry_header,\n#main_contents h2.sub_header {\n  margin: 0 0 15px;\n  padding: 5px 0 5px 35px;\n  background: url(https://blog-imgs-43.fc2.com/t/e/m/templates/content_title.png) no-repeat 20px 5px; /* 記事タイトル前のアイコン */\n}\n\n#main_contents h2.entry_header a,\n#main_contents h2.sub_header a {\n  display: block;\n  width: 100%;\n  height: 1.4em;\n  overflow: hidden;\n  white-space: nowrap;\n  text-overflow: ellipsis;\n}\n\n#main_contents h3.sub_header {\n  margin: -15px 0 20px;\n  padding: 10px 20px 10px 35px;\n  background: #F4F4F4 url(https://blog-imgs-43.fc2.com/t/e/m/templates/content_title.png) no-repeat 15px 10px; /* メインカラムのプラグインタイトル前のアイコン */\n  border-bottom: 1px dotted #B5B5B6;\n}\n\n#main_contents h4.sub_title {\n  padding: 3px 0 3px 15px;\n  margin-bottom: 20px;\n  background: url(https://blog-imgs-43.fc2.com/t/e/m/templates/content_title.png) no-repeat left 2px; /* コメント・TB見出し前のアイコン */\n}\n\n/* content style */\n#main_contents blockquote {\n  margin-bottom: 10px;\n  padding: 15px;\n  background-color: #F4F4F4;\n}\n\n#main_contents input {\n  padding: 2px;\n}\n\n/* entry_date  */\n#main_contents .entry_date {\n  float: left;\n  width: 170px;\n  margin: 0 0 10px 20px;\n  text-align: left;\n}\n\n#main_contents .entry_date li {\n  display: inline;\n  margin-right: 1em;\n}\n\n/* entry_state */\n#main_contents .entry_state {\n  float: right;\n  overflow: hidden;\n  width: 140px;\n  margin: 0 20px 10px 0;\n  text-align: right;\n}\n\n#main_contents .content .entry_state li {\n  display: inline;\n  margin-left: 1em;\n  padding-right: 1em;\n  border-right: 1px solid #999999;\n}\n\n#main_contents .content 