.plugin_profile,\r\n.section .plugin_freearea{ padding:10px; }\r\n\r\n/* Profile\r\n---------------------------------------------- */\r\ndl.profile {\r\n  background: #fff;\r\n  padding: 0;\r\n}\r\n\r\ndl.profile dt {\r\n  color:#336699;\r\n  list-style-type: none;\r\n}\r\n\r\ndl.profile dd {\r\n  list-style-type: none;\r\n  margin:0 0 20px;\r\n}\r\n\r\n/* new_entry,new_comment,new_tb\r\n---------------------------------------------- */\r\n.plugin_body .plugin_list {\r\n  color: black;\r\n  background: #fff;\r\n  padding: 0;\r\n  margin:0;\r\n  -webkit-border-bottom-right-radius: 5px;\r\n  -moz-border-bottom-right-radius: 5px;\r\n  border-bottom-right-radius: 5px;\r\n  -webkit-border-bottom-left-radius: 5px;\r\n  -moz-border-bottom-left-radius: 5px;\r\n  border-bottom-left-radius: 5px;\r\n}\r\n.plugin_body .plugin_list li {\r\n  border-top:1px solid #F2F2F2;\r\n  background: url(https://templates.blog.fc2.com/template/sphone/basic_black/chevron.png) no-repeat right center;\r\n}\r\n.plugin_body .plugin_list li a{\r\n  display: block;\r\n  overflow: hidden;\r\n  padding:10px 30px 10px 10px;\r\n}\r\n.plugin_body .plugin_list li em {\r\n  padding:0 0 3px;\r\n  font-style: normal;\r\n}\r\n.plugin_body .plugin_list li span{\r\n  color:#808080;\r\n  font-size:13px;\r\n}\r\n\r\n/*============================================================\r\n  Contents Footer Links\r\n============================================================*/\r\n/* トップ一覧表示\r\n---------------------------------------------- */\r\n.section .response {\r\n  overflow:hidden;\r\n  width:100%;\r\n  background-color:#000000;\r\n  -webkit-border-bottom-right-radius: 5px;\r\n  -moz-border-bottom-right-radius: 5px;\r\n  border-bottom-right-radius: 5px;\r\n  -webkit-border-bottom-left-radius: 5px;\r\n  -moz-border-bottom-left-radius: 5px;\r\n  border-bottom-left-radius: 5px;\r\n  font-size:13px;\r\n}\r\n.section .response li a {\r\n  display:block;\r\n  padding:10px;\r\n  color:#FFFFFF;\r\n  -webkit-tap-highlight-color:rgba(255,255,255,0.5);\r\n}\r\n.section .response li a:focus,\r\n.section .response li a:hover,\r\n.section .response li a:active {\r\n  background-color:#0044B2;\r\n}\r\n.section .response .show_com {\r\n  float:left;\r\n  width:50%;\r\n}\r\n.section .response .show_tb {\r\n  float:right;\r\n  width:50%;\r\n  text-align:right;\r\n}\r\n\r\n/* トップ一覧表示以外\r\n---------------------------------------------- */\r\n.section .contents_footer,\r\n.section .form_footer {\r\n  overflow:hidden;\r\n  width:100%;\r\n  background-color:#000000;\r\n  -webkit-border-bottom-right-radius: 5px;\r\n  -moz-border-bottom-right-radius: 5px;\r\n  border-bottom-right-radius: 5px;\r\n  -webkit-border-bottom-left-radius: 5px;\r\n  -moz-border-bottom-left-radius: 5px;\r\n  border-bottom-left-radius: 5px;\r\n}\r\n.section .contents_footer li,\r\n.section .form_footer li {\r\n  margin:-1px 0 0;\r\n  border-top:1px solid #808080;\r\n}\r\n.section .contents_footer li a,\r\n.section .form_footer li a {\r\n  display:block;\r\n  padding:10px;\r\n  color:#FFFFFF;\r\n  -webkit-tap-highlight-color:rgba(255,255,255,0.5);\r\n}\r\n.section .contents_footer li a {\r\n  padding-right:30px;\r\n  background: url(https://templates.blog.fc2.com/template/sphone/basic_black/chevron.png) no-repeat right center;\r\n}\r\n.section .form_footer li a {\r\n  text-align:center;\r\n}\r\n.section .contents_footer li a:focus,\r\n.section .contents_footer li a:hover,\r\n.section .contents_footer li a:active,\r\n.section .form_footer li a:focus,\r\n.section .form_footer li a:hover,\r\n.section .form_footer li a:active {\r\n  background-color:#0044B2;\r\n}\r\n\r\n/* コメント・トラバ非表示の場合（共通）\r\n---------------------------------------------- */\r\n.section .response li span,\r\n.section .contents_footer li span {\r\n  display:block;\r\n  padding:10px;\r\n  color:#666666;\r\n}\r\n\r\n/*============================================================\r\n  Profile (index mode)\r\n============================================================*/\r\n.profile_area {\r\n  overflow:hidden;\r\n  margin:0 2% 15px;\r\n  background-color:#FFFFFF;\r\n  -webkit-border-radius:5px;\r\n  -moz-border-radius:5px;\r\n  border-radius:5px;\r\n  -webkit-box-shadow: 2px 2px 3px rgba(0,0,0,0.3);\r\n  -moz-box-shadow: 2px 2px 3px rgba(0,0,0,0.3);\r\n  box-shadow: 2px 2px 3px rgba(0,0,0,0.3);\r\n}\r\n.profile_area dt {\r\n    margin:10px 10px 0;\r\n    padding:0 0 5px;\r\n  border-bottom:2px solid #F2F2F2;\r\n}\r\n.profile_area dd {\r\n    overflow: hidden;\r\n    padding:10px;\r\n    font-size:13px;\r\n}\r\n.profile_area dd .prof_image {\r\n  float:left;\r\n  margin-right:5px;\r\n}\r\n\r\n/*============================================================\r\n  Ad\r\n============================================================*/\r\n.ad_header{\r\n  text-align:center;\r\n  margin:10px 0;\r\n}\r\n.ad_footer{\r\n  text-align:center;\r\n  margin:10px 0;\r\n}\r\n\r\n/*============================================================\r\n  Pager\r\n============================================================*/\r\n#main_contents .pager {\r\n  position:relative;\r\n  overflow:hidden; \r\n  margin:0 0 10px;\r\n  padding:11px 0;\r\n  min-height: 30px;\r\n  text-align:center;\r\n}\r\n#main_contents .pager .prevpage {\r\n  position:absolute;\r\n  top:0;\r\n  left:0;\r\n  padding:8px 10px;\r\n}\r\n#main_contents .pager .nextpage {\r\n  position:absolute;\r\n  top:0;\r\n  right:0;\r\n  padding:8px 10px;\r\n}\r\n#main_contents .pager a {\r\n  margin:0 1px;\r\n  padding:10px 15px;\r\n  border:1px solid #000000;\r\n  -webkit-border-radius: 5px;\r\n  -moz-border-radius: 5px;\r\n  border-radius: 5px;\r\n  background: url(https://blog-imgs-42.fc2.com/t/e/m/templates/basic_black_b_bg_001.png) repeat-x left center;\r\n  background-image: -webkit-gradient(linear, left top, left bottom,\r\n    from(#AFAFAF),\r\n    color-stop(0.5,#1F1F1F),\r\n    to(#1A1A1A));\r\n  background-image:\r\n     -moz-linear-gradient(top,\r\n          #AFAFAF,\r\n          #1F1F1F 50%,\r\n          #1A1A1A\r\n     );\r\n  background-image:\r\n     -o-linear-gradient(top,\r\n          #AFAFAF,\r\n          #1F1F1F 50%,\r\n          #1A1A1A\r\n     );\r\n  -webkit-box-shadow: 1px 1px 1px #ffffff;\r\n  -moz-box-shadow: 1px 1px 1px #ffffff;\r\n  box-shadow: 1px 1px 1px #ffffff;\r\n  color:#FFFFFF;\r\n}\r\n#main_contents .pager a:focus,\r\n#main_contents .pager a:hover,\r\n#main_contents .pager a:active {\r\n  background:#0044B2 none;\r\n}\r\n#main_contents .pager strong {\r\n  margin:0 1px;\r\n  padding:10px 15px;\r\n  border:1px solid #000000;\r\n  -webkit-border-radius: 5px;\r\n  -moz-border-radius: 5px;\r\n  border-radius: 5px;\r\n  background-color:#000000;\r\n  -webkit-box-shadow: 1px 1px 1px #ffffff;\r\n  -moz-box-shadow: 1px 1px 1px #ffffff;\r\n  box-shadow: 1px 1px 1px #ffffff;\r\n  color:#FFFFFF;\r\n}\r\n\r\n/*============================================================\r\n  Page Navigation\r\n============================================================*/\r\n#page_navi {\r\n  overflow:hidden;\r\n  padding:0 2% 10px;    /* marginにするとbox-shadowがでません */\r\n}\r\n#page_navi li {\r\n  width:48%;\r\n  border: 1px solid #ffffff;\r\n  -webkit-border-radius:5px;\r\n  -moz-border-radius:5px;\r\n  border-radius:5px;\r\n  background: url(https://blog-imgs-42.fc2.com/t/e/m/templates/basic_b_bg_000.png) repeat-x left center;\r\n  background-image: -webkit-gradient(linear, left top, left bottom,\r\n    from(#FFFFFF),\r\n    color-stop(0.49,#FEFEFE),\r\n    color-stop(0.5,#EAEAEA),\r\n    to(#F9F9F9)\r\n  );\r\n  background-image:\r\n     -moz-linear-gradient(top,\r\n          #FFFFFF,\r\n          #FEFEFE 49%,\r\n          #EAEAEA 50%,\r\n          #F9F9F9\r\n     );\r\n  background-image:\r\n     -o-linear-gradient(top,\r\n          #FFFFFF,\r\n          #FEFEFE 49%,\r\n          #EAEAEA 50%,\r\n          #F9F9F9\r\n     );\r\n  -webkit-box-shadow: 2px 2px 5px rgba(0,0,0,0.3);\r\n  -moz-box-shadow: 2px 2px 5px rgba(0,0,0,0.3);\r\n  box-shadow: 2px 2px 5px rgba(0,0,0,0.3);\r\n}\r\n#page_navi .goto_home { float:left; }\r\n#page_navi .page_top { float:right; }\r\n#page_navi li a {\r\n  display:block;\r\n  padding:10px;\r\n  text-align:center;\r\n  color:#505050;\r\n}\r\n\r\n/*============================================================\r\n  Blog Footer\r\n============================================================*/\r\n#footer{\r\n  text-align:center;\r\n}\r\n#footer .footer_menu a{\r\n  margin:0 10px;\r\n}\r\n#footer .footer_menu{\r\n  margin:10px 0;\r\n}\r\n#footer .copyright {\r\n  margin:0 0 10px;\r\n  font-size:12px;\r\n}\r\n#footer .powered {\r\n  padding:10px 5px;\r\n  background-color:#000000;\r\n  text-align:center;\r\n  color:#FFFFFF;\r\n}\r\n#footer .powered a {\r\n  text-decoration:underline;\r\n}\r\n\r\n',4,'2020-07-24 11:48:03','2020-12-28 01:46:53');
INSERT INTO `blog_templates` (`id`, `blog_id`, `template_id`, `title`, `html`, `css`, `device_type`, `created_at`, `updated_at`) VALUES (3,'testblog2',0,'初期テンプレート','<!DOCTYPE html PUBLIC \"-//W3C//DTD XHTML 1.0 Strict//EN\" \"http://www.w3.org/TR/xhtml1/DTD/xhtml1-strict.dtd\">\n<html xmlns=\"http://www.w3.org/1999/xhtml\">\n<head>\n  <meta http-equiv=\"content-type\" content=\"text/html; charset=utf-8\" />\n  <meta name=\"viewport\" content=\"width=device-width, initial-scale=1.0, maximum-scale=1.0, user-scalable=no,\" />\n  <!--ios-->\n  <meta name=\"format-detection\" content=\"telephone=no\" />\n  <!--/ios-->\n  <meta name=\"description\" content=\"<%introduction>\" />\n  <title><%sub_title> - <%blog_name></title>\n  <link rel=\"icon\" href=\"https://static.fc2.com/share/image/favicon.ico\">\n  <link rel=\"stylesheet\" href=\"<%css_link>\" />\n  <link rel=\"apple-touch-icon\" href=\"https://static.fc2.com/image/iphone/apple-touch-icon.png\" />\n  <script type=\"text/javascript\" src=\"https://static.fc2.com/js/lib/jquery.js\"></script>\n  <script type=\"text/javascript\" src=\"https://static.fc2.com/js/blog/iphone_resize.js\"></script>\n  <script type=\"text/javascript\" src=\"https://static.fc2.com/js/blog/sp_acc_menu.js\"></script>\n  <script type=\"text/javascript\" charset=\"utf-8\">\n    <!--android-->\n    window.onresize = function(){\n      setOrientation();\n    }\n    <!--/android-->\n\n    window.onload = function() {\n      setTimeout(function(){if(window.pageYOffset === 0){window.scrollTo(0, 1);}}, 100);\n      setTimeout(function(){setOrientation();},300);\n    }\n    function submit(){\n      document.form1.submit();\n    }\n  </script>\n</head>\n<body>\n\n  <div id=\"header\">\n    <h1><a href=\"<%url>\"><%blog_name></a></h1>\n    <p class=\"browser_change\"><a href=\"<%url>?pc\" title=\"<%template_show_pc>\">PC</a></p>\n    <div id=\"header_menu\"><span></span></div>\n  </div>\n  <div id=\"ad_header\" class=\"ad_header\"><%ad></div>\n\n  <!--spplugin-->\n\n  <!--ヘッダーメニュー-->\n  <div id=\"plugin_menu\" class=\"section\" style=\"display:none;\">\n    <ul class=\"blogmenu\">\n    <!--プラグインメニュー--><!--spplugin_first-->\n      <li>\n        <a href=\"<%url>?mp=<%spplugin_first_no><%tail_url>\"><%spplugin_first_title></a>\n      </li>\n    <!--/spplugin_first-->\n    </ul>\n  </div>\n  <!--/spplugin-->\n\n  <div id=\"main_contents\">\n    <!--category_area--><!--カテゴリ一覧ページ-->\n      <h2 class=\"category_title\"><%sub_title></h2>\n    <!--/category_area-->\n    \n    <!--not_permanent_area-->\n    <!--記事詳細ページ以外-->\n      <!--not_spplugin_area-->\n      <!--not_comment_area-->\n      <!--not_form_area-->\n      <div class=\"section\">\n        <ul id=\"entry_list\">\n          <!--topentry-->\n          <li>\n            <a href=\"<%topentry_link><%tail_url>\" class=\"entry\">\n              <strong<!--body_img--> class=\"photo_true\"<!--/body_img-->><%topentry_title></strong>\n              <span class=\"posted\">\n                <%topentry_year>/<%topentry_month>/<%topentry_day> <%topentry_hour>:<%topentry_minute>\n              </span>\n              <span class=\"res\">\n                <!--allow_comment--><%template_comment>(<%topentry_comment_num>)<!--/allow_comment-->\n              </span>\n            </a>\n          </li>\n          <!--/topentry-->\n        </ul>\n      </div>\n      <!--/not_form_area-->\n      <!--/not_comment_area-->\n      <!--/not_spplugin_area-->\n\n<!--not_comment_area-->\n      <!--page_area-->\n        <div class=\"pager\">\n          <!--firstpage_disp--><a href=\"<%firstpage_url><%tail_url>\" class=\"prevpage\"><%firstpage_num></a>...<!--/firstpage_disp-->\n          <%template_pager1>\n          <!--lastpage_disp-->...<a href=\"<%lastpage_url><%tail_url>\" class=\"nextpage\"><%lastpage_num></a><!--/lastpage_disp-->\n        </div>\n      <!--/page_area-->\n<!--/not_comment_area-->\n    <!--/not_permanent_area-->\n\n    <!--spplugin_area-->\n    <!--プラグイン表示エリア-->\n      <!--spplugin-->\n      <div id=\"plugin\" class=\"section\">\n        <div class=\"page_title\">\n          <h2 style=\"text-align:<%spplugin_talign>; color:<%spplugin_tcolor>;\"><%spplugin_title></h2>\n        </div>\n        <div class=\"plugin_body\" style=\"text-align:<%spplugin_align>; color:<%spplugin_color>;\">\n          <%spplugin_content>\n        </div>\n      </div>\n      <!--/spplugin-->\n    <!--/プラグイン表示エリア-->\n    <!--/spplugin_area-->\n\n    <!--permanent_area-->\n    <!--エントリー個別ページ-->\n      <!--topentry-->\n      <div id=\"entry\" class=\"section\">\n        <div class=\"entry_title\">\n          <h2><%topentry_title></h2>\n          <p class=\"posted\">\n            <%topentry_year>年<%topentry_month>月<%topentry_day>日<%topentry_hour>:<%topentry_minute>&nbsp;\n            <a href=\"<%topentry_category_link>\"><%topentry_category></a>\n            <!--body_img-->\n            <a href=\"<%topentry_link>&photo=true<%tail_url>\"><img src=\"https://templates.blog.fc2.com/template/sphone/basic_black/img.gif\" alt=\"<%template_show_pic>\" width=\"18\" height=\"14\" /></a>\n            <!--/body_img-->\n          </p>\n        </div>\n        <div class=\"entry_body\">\n          <%topentry_body>\n        </div>\n    <!--more-->\n        <div class=\"entry_more\" id=\"more<%topentry_no>\">\n          <%topentry_more>\n        </div>\n    <!--/more-->\n        <ul class=\"contents_footer\">\n        <!--allow_comment-->\n          <li>\n            <a href=\"<%topentry_link>&m2=form<%tail_url>\"><%template_write_cm></a>\n          </li>\n          <li>\n            <a href=\"<%topentry_link>&m2=res<%tail_url>\"><%template_comment>(<%topentry_comment_num>)</a>\n          </li>\n        <!--/allow_comment-->\n        <!--deny_comment-->\n          <li>\n            <span><%template_cm_close></span>\n          </li>\n        <!--/deny_comment-->\n        </ul>\n      </div>\n      <div class=\"pager\">\n        <!--preventry--><a href=\"<%preventry_url><%tail_url>\" class=\"prevpage\"><%template_goto_preventry></a><!--/preventry-->\n        <!--nextentry--><a href=\"<%nextentry_url><%tail_url>\" class=\"nextpage\"><%template_goto_nextentry></a><!--/nextentry-->\n      </div>\n      <!--/topentry-->\n    <!--/permanent_area-->\n\n    <!--form_area-->\n    <!--コメント投稿ページ-->\n      <div id=\"comment_post\" class=\"section\">\n        <div class=\"page_title\">\n          <h2><%template_post_comment></h2>\n        </div>\n        <form method=\"post\" name=\"form1\" action=\"./\">\n          <dl class=\"form\">\n            <dt><%template_name></dt>\n            <dd><input type=\"text\" name=\"comment[name]\" /></dd>\n            <dt><%template_title></dt>\n            <dd><input type=\"text\" name=\"comment[title]\" /></dd>\n            <dt><%template_address></dt>\n            <dd><input type=\"email\" name=\"comment[mail]\" /></dd>\n            <dt>URL</dt>\n            <dd><input type=\"url\" name=\"comment[url]\" /></dd>\n            <dt><%template_body></dt>\n            <dd><textarea name=\"comment[body]\"></textarea></dd>\n            <dt><%template_password></dt>\n            <dd><input type=\"password\" name=\"comment[pass]\" /></dd>\n            <dt><%template_privacy_set></dt>\n            <dd>\n              <select name=\"comment[himitu]\">\n                <option value =\"0\"><%template_privacy_public></option>\n                <option value =\"1\"><%template_privacy_secret></option>\n              </select>\n            </dd>\n          </dl>\n          <input type=\"hidden\" name=\"mode\" value=\"regist\" />\n          <input type=\"hidden\" name=\"comment[no]\" value=\"<%pno>\" />\n          <input type=\"hidden\" name=\"mobile\" value=\"1\" />\n          <!--private_area--><input type=\"hidden\" name=\"spass\" value=\"<%spass>\" /><!--/private_area-->\n          <div class=\"submit_btn\">\n            <a href=\"#\" onclick=\"submit()\"><%template_send></a>\n          </div>\n        </form>\n        <ul class=\"contents_footer\">\n          <li><a href=\"<%url>?no=<%pno><%tail_url>\"><%template_return_post></a></li>\n        </ul>\n      </div>\n    <!--/form_area-->\n\n    <!--edit_area-->\n    <!--ｺﾒﾝﾄｴﾃﾞｨｯﾄｴﾘｱ開始-->\n             <div id=\"comment_post\" class=\"section\">\n                 <div class=\"page_title\">\n                     <h2><%template_edit_comment></h2>\n                 </div>\n                <form method=\"post\" name=\"form1\" action=\"./\">\n                     <dl class=\"form\">\n                        <dt><%template_name></dt>\n                         <dd><input type=\"text\" name=\"edit[name]\" value=\"<%edit_name>\" /></dd>\n                         <dt><%template_title></dt>\n                         <dd><input type=\"text\" name=\"edit[title]\" value=\"<%edit_title>\" /></dd>\n                         <dt><%template_address></dt>\n                         <dd><input type=\"email\" name=\"edit[mail]\" value=\"<%edit_mail>\" ></dd>\n                         <dt>URL</dt>\n                         <dd><input type=\"url\" name=\"edit[url]\" value=\"<%edit_url>\" /></dd>\n                         <dt><%template_body></dt>\n                         <dd><textarea name=\"edit[body]\"><%edit_body></textarea></dd>\n                         <dt><%template_password></dt>\n                         <dd><input type=\"password\" name=\"edit[pass]\" /></dd>\n                         <dt><%template_privacy_set></dt>\n                         <dd>\n                             <select name=\"edit[himitu]\">\n                                 <option value =\"0\"><%template_privacy_public></option>\n                                 <option value =\"1\"><%template_privacy_secret></option>\n                             </select>\n                         </dd>\n                     </dl>\n                     <div class=\"submit_btn\">\n                         <input type=\"submit\" value=\"<%template_update>\">\n                         <input type=\"submit\" name=\"edit[delete]\" value=\"<%template_sp_delete>\">\n                     </div>\n                     <input type=\"hidden\" name=\"mode\" value=\"edit\">\n                     <input type=\"hidden\" name=\"mode2\" value=\"edited\" />\n                     <input type=\"hidden\" name=\"edit[rno]\" value=\"<%eno>\">\n                     <input type=\"hidden\" name=\"sp\" value=\"1\" />\n                     <!--private_area--><input type=\"hidden\" name=\"spass\" value=\"<%spass>\" /><!--/private_area-->\n                 </form>\n                 <ul class=\"contents_footer\">\n                     <li><a href=\"<%url>?no=<%edit_entry_no><%tail_url>\"><%template_return_post></a></li>\n                 </ul>\n             </div>\n    <!--ｺﾒﾝﾄｴﾃﾞｨｯﾄｴﾘｱ終了-->\n    <!--/edit_area-->\n\n    <!--comment_area-->\n    <!--コメント一覧ページ-->\n      <div id=\"comment\" class=\"section\">\n        <div class=\"page_title\">\n          <h2><%template_comment></h2>\n        </div>\n        <dl class=\"list\">\n          <!--comment-->\n          <dt><%comment_title></dt>\n          <dd>\n            <%comment_body>\n            <p class=\"posted\">\n              <%comment_mail+name><!--comment_has_trip-->#<%comment_trip><!--/comment_has_trip-->\n              <%comment_url+str><br />\n              <%comment_year>/<%comment_month>/<%comment_day>(<%comment_hour>:<%comment_minute>)\n              <!--comment_edit-->\n                <a href=\"<%comment_edit_link>\" title=\"<%template_edit_comment>\">&nbsp;<%template_edit></a>\n              <!--/comment_edit-->\n            </p>\n            <!--comment_reply-->\n              <div style=\"background-color: #ffffdd; border: 1px solid #d7d7d7; padding: 5px; margin: 5px 0 0 5px;\">\n                <%comment_reply_body>\n                <p class=\"posted\">\n                  <%comment_reply_year>/<%comment_reply_month>/<%comment_reply_day>(<%comment_reply_hour>:<%comment_reply_minute>)\n                </p>\n              </div>\n  