order-radius:5px;\n  border-radius:5px;\n  background: url(https://blog-imgs-42.fc2.com/t/e/m/templates/basic_black_b_bg_001.png) repeat-x left center;\n  background-image: -webkit-gradient(linear, left top, left bottom,\n    from(#AFAFAF),\n    color-stop(0.4,#333333),\n    to(#2F2F2F));\n  background-image:\n     -moz-linear-gradient(top,\n          #AFAFAF,\n          #333333 40%,\n          #2F2F2F\n     );\n  background-image:\n     -o-linear-gradient(top,\n          #AFAFAF,\n          #333333 40%,\n          #2F2F2F\n     );\n  color: #FFFFFF;\n  font-size:12px;\n  text-align:center;\n  text-overflow: ellipsis;\n  -webkit-tap-highlight-color:rgba(255,255,255,0.5);\n}\n\n/* Header Menu Button\n---------------------------------------------- */\n#header #header_menu {\n  position:absolute;\n  top:6px;\n  left:5px;\n  width: 40px;\n  border-width:1px;\n  border-style:solid;\n  border-color:#DFDFDF #6F6F6F #3F3F3F;\n  -webkit-border-radius:5px;\n  -moz-border-radius:5px;\n  border-radius:5px;\n  background: url(https://blog-imgs-42.fc2.com/t/e/m/templates/basic_black_b_bg_001.png) repeat-x left center;\n  background-image: -webkit-gradient(linear, left top, left bottom,\n  from(#AFAFAF),\n  color-stop(0.4,#333333),\n  to(#2F2F2F));\n  background-image:\n  -moz-linear-gradient(top,\n    #AFAFAF,\n    #333333 40%,\n    #2F2F2F\n  );\n  background-image:\n  -o-linear-gradient(top,\n    #AFAFAF,\n    #333333 40%,\n    #2F2F2F\n  );\n  color: #FFFFFF;\n  cursor:pointer;\n}\n#header #header_menu span {\n  display:block;\n  height:30px;\n  background:url(https://templates.blog.fc2.com/template/sphone/basic_black/dropmenu.png) no-repeat center 12px;\n  -webkit-tap-highlight-color:rgba(255,255,255,0.5);\n}\n#header #header_menu.selected {\n  border:none;\n  background-image:\n    -webkit-gradient(linear, left top, left bottom,\n      from(#2F2F2F),\n      to(#505050)\n    );\n  background-image:\n     -moz-linear-gradient(top,\n      #2F2F2F,\n      #505050\n     );\n  bac